# Добавляем существующие лоты

# Добавляем ставки


# Получаем все категории
select * from categories;

# Получаем самые новые, открытые лоты. Каждый лот должен включать название, стартовую цену, ссылку на изображение, цену, название категории;
select l.title, start_price, lot_img, c.title from lots l join categories c on l.id_category = c.id where date_reg < stop_date  order by date_reg desc;

# Показываем лот по его id. Получите также название категории, к которой принадлежит лот;
select l.*, c.title  from lots l  join categories c on l.id_category = c.id where l.id = 1;

# Обновляем название лота по его идентификатору;
update lots set title = 'Самая лучшая карбоновая телескопическая палка' where id = 1;

# Получаем список самых свежих ставок для лота по его идентификатору.
select * from bets where id_lot = 1 order by bet_date desc;